package com.common.projectcommonframe.components;import android.content.Context;import android.content.SharedPreferences;/** * SharePreference工具类 */public class BufferedSharedPreferences {    private final SharedPreferences sp;    public BufferedSharedPreferences(SharedPreferences sp){        this.sp = sp;    }    public void saveString(String key, String value) {        sp.edit().putString(key, value).apply();    }    public String getString(String key, String defValue){        return sp.getString(key, defValue);    }    public void saveInt(String key, int value){        sp.edit().putInt(key, value).apply();    }    public int getInt(String key, int defValue){        return sp.getInt(key, defValue);    }    public void saveBoolean(String key, boolean value){        sp.edit().putBoolean(key, value).apply();    }    public boolean getBoolean(String key, boolean defValue){        return sp.getBoolean(key, defValue);    }    public void saveLong(String key , long value){        sp.edit().putLong(key,value).apply();    }    public long getLong(String key , long defValue){        return sp.getLong(key,defValue);    }    public void saveFloat(String key , float value){        sp.edit().putFloat(key,value).apply();    }    public float getFloat(String key , float defValue){        return sp.getFloat(key,defValue);    }    public void remove(String key){        sp.edit().remove(key).apply();    }    public void clear(){        sp.edit().clear().apply();    }    /**     * 实例化对象     * @param context  上下文     * @param name   SharePreference 名字     * @return     */    public static BufferedSharedPreferences getInstance(Context context, String name){        return new BufferedSharedPreferences(context.getSharedPreferences(name, Context.MODE_PRIVATE));    }}