package com.common.projectcommonframe.view;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import java.util.List;/** * Fragment Adapter */public class MyFragmentPagerAdapter extends FragmentPagerAdapter {    private List<? extends Fragment> fragments;    private String[] titles;    public MyFragmentPagerAdapter(FragmentManager fm, List<? extends Fragment> fragments) {        super(fm);        this.fragments = fragments;    }    public MyFragmentPagerAdapter(FragmentManager fm, List<? extends Fragment> fragments, String[] titles) {        super(fm);        this.fragments = fragments;        this.titles = titles;    }    @Override    public Fragment getItem(int position) {        return fragments.get(position);    }    @Override    public int getCount() {        return fragments==null?0:fragments.size();    }    @Override    public CharSequence getPageTitle(int position) {        if (titles!=null){            return titles[position];        }        return super.getPageTitle(position);    }}